(6, 1, '2015-07-10 06:00:19', '2015-07-10 06:00:19', 'a:15:{s:4:"type";s:5:"image";s:12:"instructions";s:23:"Ảnh bìa sản phẩm";s:8:"required";i:1;s:17:"conditional_logic";i:0;s:7:"wrapper";a:3:{s:5:"width";s:0:"";s:5:"class";s:0:"";s:2:"id";s:0:"";}s:13:"return_format";s:3:"url";s:12:"preview_size";s:6:"medium";s:7:"library";s:3:"all";s:9:"min_width";s:0:"";s:10:"min_height";s:0:"";s:8:"min_size";s:0:"";s:9:"max_width";s:0:"";s:10:"max_height";s:0:"";s:8:"max_size";s:0:"";s:10:"mime_types";s:0:"";}', 'Ảnh bìa', 'anhbia', 'publish', 'open', 'open', '', 'field_559f5ead17e17', '', '', '2015-07-10 06:00:19', '2015-07-10 06:00:19', '', 5, 'http://localhost/ntcorp/?post_type=acf-field&p=6', 0, 'acf-field', '', 0),
